import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [url, setUrl] = useState('');
  const [format, setFormat] = useState('video1080');
  const [loading, setLoading] = useState(false);

  const handleDownload = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/api/download', { url, format }, { responseType: 'blob' });
      const blob = new Blob([response.data]);
      const downloadUrl = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = downloadUrl;
      a.download = `download.${format.includes('audio') ? 'mp3' : 'mp4'}`;
      document.body.appendChild(a);
      a.click();
      a.remove();
    } catch (err) {
      alert('Download failed');
    }
    setLoading(false);
  };

  return (
    <div style={{ textAlign: 'center', padding: '50px' }}>
      <h2>YouTube Downloader (MERN Stack)</h2>
      <form onSubmit={handleDownload}>
        <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} placeholder="Enter YouTube URL" required style={{ padding: 10, width: 300 }} />
        <br /><br />
        <select value={format} onChange={(e) => setFormat(e.target.value)} style={{ padding: 10 }}>
          <option value="video2160">Video - 2160p</option>
          <option value="video1440">Video - 1440p</option>
          <option value="video1080">Video - 1080p</option>
          <option value="video720">Video - 720p</option>
          <option value="video480">Video - 480p</option>
          <option value="video360">Video - 360p</option>
          <option value="video240">Video - 240p</option>
          <option value="video144">Video - 144p</option>
          <option value="audio">Audio - MP3</option>
        </select>
        <br /><br />
        <button type="submit" style={{ padding: '10px 20px' }}>{loading ? 'Downloading...' : 'Download'}</button>
      </form>
    </div>
  );
};

export default App;
